// Page2.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CodeModal from './CodeModal'; // Import the modal component

const Page2 = () => {
  const [snippets, setSnippets] = useState([]);
  const [selectedCode, setSelectedCode] = useState(null); // State to manage which code to display in the modal

  useEffect(() => {
    const fetchSnippets = async () => {
      try {
        const response = await axios.get('http://localhost:3001/snippets');
        setSnippets(response.data);
      } catch (error) {
        console.error('Error fetching snippets:', error);
      }
    };

    fetchSnippets();
  }, []);

  const openCodeModal = (code) => {
    setSelectedCode(code);
  };

  const closeCodeModal = () => {
    setSelectedCode(null);
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-bold mb-5">Submitted Snippets</h1>
      <table className="table-auto w-full">
        <thead>
          <tr>
            <th className="px-4 py-2">Username</th>
            <th className="px-4 py-2">Language</th>
            <th className="px-4 py-2">Standard Input</th>
            <th className="px-4 py-2">Source Code</th>
            <th className="px-4 py-2">Timestamp</th>
          </tr>
        </thead>
        <tbody>
          {snippets.map((snippet, index) => (
            <tr key={index}>
              <td className="border px-4 py-2">{snippet.username}</td>
              <td className="border px-4 py-2">{snippet.language}</td>
              <td className="border px-4 py-2">
                {snippet.stdin.map((input, index) => (
                  <div key={index}>{input}</div>
                ))}
              </td>
              <td className="border px-4 py-2">
                {snippet.code.substring(0, 100)}...
                <button
                  className="ml-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => openCodeModal(snippet.code)}
                >
                  View
                </button>
              </td>
              <td className="border px-4 py-2">{snippet.timestamp}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedCode && (
        <CodeModal code={selectedCode} onClose={closeCodeModal} />
      )}
    </div>
  );
};

export default Page2;
